import React, { useState } from "react";
import { Container, Box, TextField, Button, Typography, Alert } from "@mui/material";
import axios from "axios";

function App() {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const [success, setSuccess] = useState(false);

  const handleLogin = async (e) => {
    e.preventDefault();
    setError("");
    try {
      const res = await axios.post("http://localhost:5000/api/login", { username, password });
      if (res.data.success) {
        setSuccess(true);
      }
    } catch (err) {
      setError(err.response?.data?.message || "Sunucu hatası.");
    }
  };

  if (success) {
    return <TahsilatForm username={username} />;
  }

  return (
    <Container maxWidth="xs">
      <Box sx={{ mt: 8, p: 4, bgcolor: "#f5f5f5", borderRadius: 2, boxShadow: 2 }}>
        <Typography variant="h5" align="center" gutterBottom>Giriş Yap</Typography>
        <form onSubmit={handleLogin}>
          <TextField label="Kullanıcı Adı" fullWidth margin="normal" value={username} onChange={e => setUsername(e.target.value)} required />
          <TextField label="Şifre" type="password" fullWidth margin="normal" value={password} onChange={e => setPassword(e.target.value)} required />
          {error && <Alert severity="error">{error}</Alert>}
          <Button type="submit" variant="contained" color="primary" fullWidth sx={{ mt: 2 }}>Giriş Yap</Button>
        </form>
      </Box>
    </Container>
  );
}

function TahsilatForm({ username }) {
  const [clcards, setClcards] = useState([]);
  const [clcardFilter, setClcardFilter] = useState("");
  const [selectedClcard, setSelectedClcard] = useState("");
  const [evrakNo, setEvrakNo] = useState("");
  const [tahsilatTuru, setTahsilatTuru] = useState("");
  const [banka, setBanka] = useState("");
  const [taksit, setTaksit] = useState(1);
  const [tutar, setTutar] = useState("");
  const [tarih, setTarih] = useState(() => new Date().toISOString().slice(0, 10));

  const tahsilatTurleri = ["NAKİT", "KREDİ KARTI", "HAVALE", "ÇEK", "SENET"];
  const bankaBilgileri = [
    "YAPIKREDİ", "AKBANK", "FİNANSBANK", "DENİZBANK", "GARANTİ", "İŞBANKASI", "TEB", "VAKIFBANK", "MRKBANK",
    "GARANTİ ŞAHSİ", "İŞBANKASI ŞAHSİ", "ZİRAAT"
  ];

  React.useEffect(() => {
    axios.get(`http://localhost:5000/api/clcard?username=${username}`)
      .then(res => setClcards(res.data))
      .catch(() => setClcards([]));
  }, [username]);

  // Filtreli açılır liste
  const filteredClcards = clcards.filter(c =>
    (c.CODE + " " + c.DEFINITION_ + " " + c.SPECODE).toLowerCase().includes(clcardFilter.toLowerCase())
  );

  // Alanların aktif/pasif durumu
  const isBankaActive = tahsilatTuru === "KREDİ KARTI";
  const isTaksitActive = tahsilatTuru === "KREDİ KARTI";
  const isBankaVisible = tahsilatTuru !== "NAKİT";
  const isTaksitVisible = tahsilatTuru !== "HAVALE" && tahsilatTuru !== "ÇEK" && tahsilatTuru !== "SENET" && tahsilatTuru !== "NAKİT";

  return (
    <Container maxWidth="sm">
      <Box sx={{ mt: 8, p: 4, bgcolor: "#e3f2fd", borderRadius: 2, boxShadow: 2 }}>
        <Alert severity="success">Giriş başarılı!</Alert>
        <Typography variant="h6" sx={{ mt: 2 }}>Tahsilat Formu</Typography>
        <Box component="form" sx={{ mt: 2, display: "flex", flexDirection: "column", gap: 2 }}>
          {/* Cari seç autocomplete */}
          <Box>
            <TextField
              label="Cari Seç"
              value={clcardFilter}
              onChange={e => setClcardFilter(e.target.value)}
              fullWidth
              autoComplete="off"
            />
            <TextField
              select
              label="Cari Listesi"
              value={selectedClcard}
              onChange={e => setSelectedClcard(e.target.value)}
              SelectProps={{ native: true }}
              fullWidth
              sx={{ mt: 1 }}
            >
              <option value="">Seçiniz</option>
              {clcards.filter(c =>
                (c.CODE + " " + c.DEFINITION_ + " " + c.SPECODE).toLowerCase().includes(clcardFilter.toLowerCase())
              ).map(c => (
                <option key={c.CODE} value={c.CODE}>{c.CODE} - {c.DEFINITION_} ({c.SPECODE})</option>
              ))}
            </TextField>
          </Box>
          {/* Evrak No */}
          <TextField label="Evrak No" fullWidth value={evrakNo} onChange={e => setEvrakNo(e.target.value)} />
          {/* Tahsilat Türü */}
          <TextField
            select
            label="Tahsilat Türü"
            value={tahsilatTuru}
            onChange={e => setTahsilatTuru(e.target.value)}
            SelectProps={{ native: true }}
            fullWidth
            required
          >
            <option value="">Seçiniz</option>
            {tahsilatTurleri.map(t => <option key={t} value={t}>{t}</option>)}
          </TextField>
          {/* Banka Bilgileri */}
          {isBankaVisible && (
            <TextField
              select
              label="Banka Bilgileri"
              value={banka}
              onChange={e => setBanka(e.target.value)}
              SelectProps={{ native: true }}
              fullWidth
              disabled={!isBankaActive}
            >
              <option value="">Seçiniz</option>
              {bankaBilgileri.map(b => <option key={b} value={b}>{b}</option>)}
            </TextField>
          )}
          {/* Taksit Sayısı */}
          {isTaksitVisible && (
            <TextField
              select
              label="Taksit Sayısı"
              value={taksit}
              onChange={e => setTaksit(Number(e.target.value))}
              SelectProps={{ native: true }}
              fullWidth
              disabled={!isTaksitActive}
            >
              {[...Array(12)].map((_, i) => <option key={i+1} value={i+1}>{i+1}</option>)}
            </TextField>
          )}
          {/* Tutar */}
          <TextField
            label="Tutar"
            value={tutar}
            onChange={e => {
              const val = e.target.value;
              if (/^\d*$/.test(val)) setTutar(val);
            }}
            fullWidth
            required
          />
          {/* Tarih */}
          <TextField
            label="Tarih"
            type="date"
            value={tarih}
            onChange={e => setTarih(e.target.value)}
            fullWidth
            InputLabelProps={{ shrink: true }}
          />
          <Button variant="contained" color="primary" sx={{ mt: 2 }}>Kaydet</Button>
        </Box>
      </Box>
    </Container>
  );
}

export default App;
